//b. if-else statement
public class Conditionals2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int number = 13;
		if (number%2==0) {
			System.out.println("Even number");//Executes, if the given condition is true
		}
		else {
			System.out.println("Odd number"); // Executes if the above "if statement" is false.
		}

	}

}
